","keccak256":"0x006dd67219697fe68d7fbfdea512e7c4cb64a43565ed86171d67e844982da6fa","license":"MIT"},"contracts/changers/FlowChangeProposal.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import { CommissionSplitter } from "moc-main/contracts/auxiliary/CommissionSplitter.sol";
import { IChangerContract } from "../interfaces/IChangerContract.sol";

/**
  In this changer we change:

  1) Set Fee flow output to new commission splitter
  2) Set TCInterest output to new commission splitter
  3) New implementation of MoCQueue (fix bug)
  4) New feeTokenPriceProvider get the price from OKU swap
  5) Update amount of blocks to wait for next TC interest payment
  6) Update amount of blocks between settlements
  7) Update Flux capacitor decay block span
  8) Update how many blocks should pass between EMA calculations
 */

interface IMocCARC20 {
    function setMocFeeFlowAddress(address mocFeeFlowAddress_) external;

    function setTCInterestCollectorAddress(address tcInterestCollectorAddress_) external;

    function setFeeTokenPriceProviderAddress(address mocFeeTokenPriceProviderAddress_) external;

    function mocQueue() external view returns (address mocQueue_);

    function setTCInterestPaymentBlockSpan(uint256 tcInterestPaymentBlockSpan_) external;

    function setBes(uint256 bes_) external;

    function setDecayBlockSpan(uint256 decayBlockSpan_) external;

    function setEmaCalculationBlockSpan(uint256 blockSpan_) external;
}

interface IMocQueue {
     function upgradeTo(address newMocQueueImpl_) external;
}


contract FlowChangeProposal is IChangerContract {
    // ------- Storage -------

    // MocCore proxy contract
    IMocCARC20 public immutable mocCoreProxy;
    // MocQueue proxy contract
    IMocQueue public immutable mocQueueProxy;
    // new MocQueue implementation contract
    address public immutable newMocQueueImpl;
    // Fee Token price provider
    address public feeTokenPriceProvider;
    // New operations fees splitter for both TCInterest & MoCFee collector
    address public immutable feesSplitterProxy;
    // Amount of blocks to wait for next TC interest payment
    uint256 public tCInterestPaymentBlockSpan;
    // Number of blocks between settlements
    uint256 public settlementBlockSpan;
    // Flux capacitor decay block span
    uint256 public decayBlockSpan;
    // How many blocks should pass between EMA calculations
    uint256 public emaCalculationBlockSpan;

    /**
     * @notice constructor
     * @param mocCoreProxy_ MocCore proxy contract
     * @param newMocQueueImpl_ new MocQueue implementation contract
     * @param feeTokenPriceProvider_ new Fee Token price provider address
     * @param feesSplitterProxy_ new Commission splitter for both TCInterest & MoCFee collector
     * @param tCInterestPaymentBlockSpan_ Amount of blocks to wait for next TC interest payment
     * @param settlementBlockSpan_ Number of blocks between settlements
     * @param decayBlockSpan_ Flux capacitor decay block span
     * @param emaCalculationBlockSpan_ How many blocks should pass between EMA calculations
     */
    constructor(
        IMocCARC20 mocCoreProxy_,
        address newMocQueueImpl_,
        address feeTokenPriceProvider_,
        address feesSplitterProxy_,
        uint256 tCInterestPaymentBlockSpan_,
        uint256 settlementBlockSpan_,
        uint256 decayBlockSpan_,
        uint256 emaCalculationBlockSpan_
    ) {
        mocCoreProxy = mocCoreProxy_;
        newMocQueueImpl = newMocQueueImpl_;
        feeTokenPriceProvider = feeTokenPriceProvider_;
        feesSplitterProxy = feesSplitterProxy_;
        tCInterestPaymentBlockSpan = tCInterestPaymentBlockSpan_;
        settlementBlockSpan = settlementBlockSpan_;
        decayBlockSpan = decayBlockSpan_;
        emaCalculationBlockSpan = emaCalculationBlockSpan_;
        mocQueueProxy = IMocQueue(mocCoreProxy.mocQueue());
    }

    /**
      @notice Execute the changes.
      @dev Should be called by the governor, but this contract does not check that explicitly
      because it is not its responsibility in the current architecture
      IMPORTANT: This function should not be overridden, you should only redefine
      _beforeUpgrade and _afterUpgrade methods to use this template
    */
    function execute() external {
        _beforeUpgrade();
        _upgrade();
        _afterUpgrade();
    }

    /**
      @notice Upgrade the proxy to the newImplementation
      @dev IMPORTANT: This function should not be overridden
    */
    function _upgrade() internal {
        mocQueueProxy.upgradeTo(newMocQueueImpl);
    }

    /**
      @notice Intended to prepare the system for the upgrade
    */
    function _beforeUpgrade() internal view {}

    /**
      @notice Intended to do the final tweaks after the upgrade, for example initialize the contract
    */
    function _afterUpgrade() internal {
        // update MocCore setups
        mocCoreProxy.setMocFeeFlowAddress(feesSplitterProxy);
        mocCoreProxy.setTCInterestCollectorAddress(feesSplitterProxy);
        mocCoreProxy.setFeeTokenPriceProviderAddress(feeTokenPriceProvider);

        mocCoreProxy.setTCInterestPaymentBlockSpan(tCInterestPaymentBlockSpan);
        mocCoreProxy.setBes(settlementBlockSpan);
        mocCoreProxy.setDecayBlockSpan(decayBlockSpan);
        mocCoreProxy.setEmaCalculationBlockSpan(emaCalculationBlockSpan);
    }

}



","keccak256":"0xbeb8669f9df9f150eebce8fe3078a8895519fec633faeaef3561580532bd3355","license":"UNLICENSED"},"contracts/interfaces/IChangerContract.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

/**
  @title IChangerContract
  @notice This interface is the one used by the governance system.
  @dev If you plan to do some changes to a system governed by this project you should write a contract
  that does those changes, like a recipe. This contract MUST not have ANY kind of public or external function
  that modifies the state of this ChangeContract, otherwise you could run into front-running issues when the governance
  system is fully in place.
 */
interface IChangerContract {
    /**
    @notice Override this function with a recipe of the changes to be done when this ChangeContract
    is executed
   */
    function execute() external;
}
","keccak256":"0x2f30103bbdefcd63e9a98e7462ec95c54df9455fa086e789af0a0f6483daae79","license":"UNLICENSED"},"moc-main/contracts/auxiliary/CommissionSplitter.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import { Governed } from "../governance/Governed.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
  @dev Contract that split his balance between two addresses based on a
  proportion defined by Governance.
 */
contract CommissionSplitter is Governed, UUPSUpgradeable {
    // ------- Events -------

    event SplitExecuted(
        uint256 acTokenPctToRecipient1_,
        uint256 acTokenPctToRecipient2_,
        uint256 feeTokenPctToRecipient1_,
        uint256 feeTokenPctToRecipient2_
    );

    // ------- Storage -------

    // collateral asset
    IERC20 public acToken;
    // fee token
    IERC20 public feeToken;

    // address who receives acTokenPctToRecipient1
    address public acTokenAddressRecipient1;
    // address who receives the rest of the commissions (100% - acTokenPctToRecipient1)
    address public acTokenAddressRecipient2;
    // percentage of the acToken balance to send to acTokenAddressRecipient1 [PREC]
    uint256 public acTokenPctToRecipient1;

    // address who receives feeTokenPctToRecipient1
    address public feeTokenAddressRecipient1;
    // address who receives the rest of the commissions (100% - feeTokenPctToRecipient1)
    address public feeTokenAddressRecipient2;
    // percentage of the feeToken balance to send to acTokenAddressRecipient1 [PREC]
    uint256 public feeTokenPctToRecipient1;

    /**
     * @notice contract initializer
     * @param governorAddress_ governor address
     * @param acToken_ collateral asset contract
     * @param feeToken_ fee token contract
     * @param acTokenAddressRecipient1_ address who receives acTokenPctToRecipient1
     * @param acTokenAddressRecipient2_ address who receives (100% - acTokenPctToRecipient1)
     * @param acTokenPctToRecipient1_ percentage of the acToken balance to send to acTokenAddressRecipient1 [PREC]
     * @param feeTokenAddressRecipient1_ address who receives feeTokenPctToRecipient1
     * @param feeTokenAddressRecipient2_ address who receives (100% - feeTokenPctToRecipient1)
     * @param feeTokenPctToRecipient1_ percentage of the feeToken balance to send to acTokenAddressRecipient1 [PREC]
     */
    function initialize(
        address governorAddress_,
        IERC20 acToken_,
        IERC20 feeToken_,
        address acTokenAddressRecipient1_,
        address acTokenAddressRecipient2_,
        uint256 acTokenPctToRecipient1_,
        address feeTokenAddressRecipient1_,
        address feeTokenAddressRecipient2_,
        uint256 feeTokenPctToRecipient1_
    ) public initializer {
        if (acTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        if (feeTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        __UUPSUpgradeable_init();
        __Governed_init(governorAddress_);

        acToken = acToken_;
        feeToken = feeToken_;
        acTokenAddressRecipient1 = acTokenAddressRecipient1_;
        acTokenAddressRecipient2 = acTokenAddressRecipient2_;
        acTokenPctToRecipient1 = acTokenPctToRecipient1_;
        feeTokenAddressRecipient1 = feeTokenAddressRecipient1_;
        feeTokenAddressRecipient2 = feeTokenAddressRecipient2_;
        feeTokenPctToRecipient1 = feeTokenPctToRecipient1_;
    }

    /**
     * @notice splits all the acToken and feeToken balances to the recipients addresses
     */
    function split() external {
        uint256 acTokenAmountToRecipient1;
        uint256 acTokenAmountToRecipient2;
        uint256 feeTokenAmountToRecipient1;
        uint256 feeTokenAmountToRecipient2;

        // splits collateral asset
        uint256 acTokenBalance = acToken.balanceOf(address(this));
        if (acTokenBalance > 0) {
            acTokenAmountToRecipient1 = _mulPrec(acTokenBalance, acTokenPctToRecipient1);
            acTokenAmountToRecipient2 = acTokenBalance - acTokenAmountToRecipient1;
            SafeERC20.safeTransfer(acToken, acTokenAddressRecipient1, acTokenAmountToRecipient1);
            SafeERC20.safeTransfer(acToken, acTokenAddressRecipient2, acTokenAmountToRecipient2);
        }

        // splits fee token
        uint256 feeTokenBalance = feeToken.balanceOf(address(this));
        if (feeTokenBalance > 0) {
            feeTokenAmountToRecipient1 = _mulPrec(feeTokenBalance, feeTokenPctToRecipient1);
            feeTokenAmountToRecipient2 = feeTokenBalance - feeTokenAmountToRecipient1;
            SafeERC20.safeTransfer(feeToken, feeTokenAddressRecipient1, feeTokenAmountToRecipient1);
            SafeERC20.safeTransfer(feeToken, feeTokenAddressRecipient2, feeTokenAmountToRecipient2);
        }

        emit SplitExecuted(
            acTokenAmountToRecipient1,
            acTokenAmountToRecipient2,
            feeTokenAmountToRecipient1,
            feeTokenAmountToRecipient2
        );
    }

    // ------- Only Authorized Changer Functions -------

    /** @notice sets new AC token
     * @param newAcToken_ new AC token contract
     */
    function setAcToken(IERC20 newAcToken_) external onlyAuthorizedChanger {
        acToken = newAcToken_;
    }

    /** @notice sets new fee token
     * @param newFeeToken_ new fee token contract
     */
    function setFeeToken(IERC20 newFeeToken_) external onlyAuthorizedChanger {
        feeToken = newFeeToken_;
    }

    /** @notice sets new recipient1 for AC token
     * @param acTokenAddressRecipient1_ new recipient1 address
     */
    function setAcTokenAddressRecipient1(address acTokenAddressRecipient1_) external onlyAuthorizedChanger {
        acTokenAddressRecipient1 = acTokenAddressRecipient1_;
    }

    /** @notice sets new recipient2 for AC token
     * @param acTokenAddressRecipient2_ new recipient1 address
     */
    function setAcTokenAddressRecipient2(address acTokenAddressRecipient2_) external onlyAuthorizedChanger {
        acTokenAddressRecipient2 = acTokenAddressRecipient2_;
    }

    /** @notice sets new percentage of AC token for recipient1
     * @param acTokenPctToRecipient1_ new percentage for recipient1 [PREC]
     */
    function setAcTokenPctToRecipient1(uint256 acTokenPctToRecipient1_) external onlyAuthorizedChanger {
        if (acTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        acTokenPctToRecipient1 = acTokenPctToRecipient1_;
    }

    /** @notice sets new recipient1 for fee token
     * @param feeTokenAddressRecipient1_ new recipient1 address
     */
    function setFeeTokenAddressRecipient1(address feeTokenAddressRecipient1_) external onlyAuthorizedChanger {
        feeTokenAddressRecipient1 = feeTokenAddressRecipient1_;
    }

    /** @notice sets new recipient2 for fee token
     * @param feeTokenAddressRecipient2_ new recipient2 address
     */
    function setFeeTokenAddressRecipient2(address feeTokenAddressRecipient2_) external onlyAuthorizedChanger {
        feeTokenAddressRecipient2 = feeTokenAddressRecipient2_;
    }

    /** @notice sets new percentage of fee token for recipient1
     * @param feeTokenPctToRecipient1_ new percentage for recipient1 [PREC]
     */
    function setFeeTokenPctToRecipient1(uint256 feeTokenPctToRecipient1_) external onlyAuthorizedChanger {
        if (feeTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        feeTokenPctToRecipient1 = feeTokenPctToRecipient1_;
    }

    /**
     * @inheritdoc UUPSUpgradeable
     * @dev checks that the changer that will do the upgrade is currently authorized by governance to makes
     * changes within the system
     * @param newImplementation new implementation contract address(not used)
     */
    /* solhint-disable-next-line no-empty-blocks */
    function _authorizeUpgrade(address newImplementation) internal override onlyAuthorizedChanger {}

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */

    // Purposely left unused to save some state space to allow for future upgrades
    // slither-disable-next-line unused-state
    uint256[50] private __gap;
}
