moc-main/contracts/auxiliary/CommissionS.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import { Governed } from "../governance/Governed.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
  @dev Contract that split his balance between two addresses based on a
  proportion defined by Governance.
 */
contract CommissionSplitter is Governed, UUPSUpgradeable {
    // ------- Events -------

    event SplitExecuted(
        uint256 acTokenPctToRecipient1_,
        uint256 acTokenPctToRecipient2_,
        uint256 feeTokenPctToRecipient1_,
        uint256 feeTokenPctToRecipient2_
    );

    // ------- Storage -------

    // collateral asset
    IERC20 public acToken;
    // fee token
    IERC20 public feeToken;

    // address who receives acTokenPctToRecipient1
    address public acTokenAddressRecipient1;
    // address who receives the rest of the commissions (100% - acTokenPctToRecipient1)
    address public acTokenAddressRecipient2;
    // percentage of the acToken balance to send to acTokenAddressRecipient1 [PREC]
    uint256 public acTokenPctToRecipient1;

    // address who receives feeTokenPctToRecipient1
    address public feeTokenAddressRecipient1;
    // address who receives the rest of the commissions (100% - feeTokenPctToRecipient1)
    address public feeTokenAddressRecipient2;
    // percentage of the feeToken balance to send to acTokenAddressRecipient1 [PREC]
    uint256 public feeTokenPctToRecipient1;

    /**
     * @notice contract initializer
     * @param governorAddress_ governor address
     * @param acToken_ collateral asset contract
     * @param feeToken_ fee token contract
     * @param acTokenAddressRecipient1_ address who receives acTokenPctToRecipient1
     * @param acTokenAddressRecipient2_ address who receives (100% - acTokenPctToRecipient1)
     * @param acTokenPctToRecipient1_ percentage of the acToken balance to send to acTokenAddressRecipient1 [PREC]
     * @param feeTokenAddressRecipient1_ address who receives feeTokenPctToRecipient1
     * @param feeTokenAddressRecipient2_ address who receives (100% - feeTokenPctToRecipient1)
     * @param feeTokenPctToRecipient1_ percentage of the feeToken balance to send to acTokenAddressRecipient1 [PREC]
     */
    function initialize(
        address governorAddress_,
        IERC20 acToken_,
        IERC20 feeToken_,
        address acTokenAddressRecipient1_,
        address acTokenAddressRecipient2_,
        uint256 acTokenPctToRecipient1_,
        address feeTokenAddressRecipient1_,
        address feeTokenAddressRecipient2_,
        uint256 feeTokenPctToRecipient1_
    ) public initializer {
        if (acTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        if (feeTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        __UUPSUpgradeable_init();
        __Governed_init(governorAddress_);

        acToken = acToken_;
        feeToken = feeToken_;
        acTokenAddressRecipient1 = acTokenAddressRecipient1_;
        acTokenAddressRecipient2 = acTokenAddressRecipient2_;
        acTokenPctToRecipient1 = acTokenPctToRecipient1_;
        feeTokenAddressRecipient1 = feeTokenAddressRecipient1_;
        feeTokenAddressRecipient2 = feeTokenAddressRecipient2_;
        feeTokenPctToRecipient1 = feeTokenPctToRecipient1_;
    }

    /**
     * @notice splits all the acToken and feeToken balances to the recipients addresses
     */
    function split() external {
        uint256 acTokenAmountToRecipient1;
        uint256 acTokenAmountToRecipient2;
        uint256 feeTokenAmountToRecipient1;
        uint256 feeTokenAmountToRecipient2;

        // splits collateral asset
        uint256 acTokenBalance = acToken.balanceOf(address(this));
        if (acTokenBalance > 0) {
            acTokenAmountToRecipient1 = _mulPrec(acTokenBalance, acTokenPctToRecipient1);
            acTokenAmountToRecipient2 = acTokenBalance - acTokenAmountToRecipient1;
            SafeERC20.safeTransfer(acToken, acTokenAddressRecipient1, acTokenAmountToRecipient1);
            SafeERC20.safeTransfer(acToken, acTokenAddressRecipient2, acTokenAmountToRecipient2);
        }

        // splits fee token
        uint256 feeTokenBalance = feeToken.balanceOf(address(this));
        if (feeTokenBalance > 0) {
            feeTokenAmountToRecipient1 = _mulPrec(feeTokenBalance, feeTokenPctToRecipient1);
            feeTokenAmountToRecipient2 = feeTokenBalance - feeTokenAmountToRecipient1;
            SafeERC20.safeTransfer(feeToken, feeTokenAddressRecipient1, feeTokenAmountToRecipient1);
            SafeERC20.safeTransfer(feeToken, feeTokenAddressRecipient2, feeTokenAmountToRecipient2);
        }

        emit SplitExecuted(
            acTokenAmountToRecipient1,
            acTokenAmountToRecipient2,
            feeTokenAmountToRecipient1,
            feeTokenAmountToRecipient2
        );
    }

    // ------- Only Authorized Changer Functions -------

    /** @notice sets new AC token
     * @param newAcToken_ new AC token contract
     */
    function setAcToken(IERC20 newAcToken_) external onlyAuthorizedChanger {
        acToken = newAcToken_;
    }

    /** @notice sets new fee token
     * @param newFeeToken_ new fee token contract
     */
    function setFeeToken(IERC20 newFeeToken_) external onlyAuthorizedChanger {
        feeToken = newFeeToken_;
    }

    /** @notice sets new recipient1 for AC token
     * @param acTokenAddressRecipient1_ new recipient1 address
     */
    function setAcTokenAddressRecipient1(address acTokenAddressRecipient1_) external onlyAuthorizedChanger {
        acTokenAddressRecipient1 = acTokenAddressRecipient1_;
    }

    /** @notice sets new recipient2 for AC token
     * @param acTokenAddressRecipient2_ new recipient1 address
     */
    function setAcTokenAddressRecipient2(address acTokenAddressRecipient2_) external onlyAuthorizedChanger {
        acTokenAddressRecipient2 = acTokenAddressRecipient2_;
    }

    /** @notice sets new percentage of AC token for recipient1
     * @param acTokenPctToRecipient1_ new percentage for recipient1 [PREC]
     */
    function setAcTokenPctToRecipient1(uint256 acTokenPctToRecipient1_) external onlyAuthorizedChanger {
        if (acTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        acTokenPctToRecipient1 = acTokenPctToRecipient1_;
    }

    /** @notice sets new recipient1 for fee token
     * @param feeTokenAddressRecipient1_ new recipient1 address
     */
    function setFeeTokenAddressRecipient1(address feeTokenAddressRecipient1_) external onlyAuthorizedChanger {
        feeTokenAddressRecipient1 = feeTokenAddressRecipient1_;
    }

    /** @notice sets new recipient2 for fee token
     * @param feeTokenAddressRecipient2_ new recipient2 address
     */
    function setFeeTokenAddressRecipient2(address feeTokenAddressRecipient2_) external onlyAuthorizedChanger {
        feeTokenAddressRecipient2 = feeTokenAddressRecipient2_;
    }

    /** @notice sets new percentage of fee token for recipient1
     * @param feeTokenPctToRecipient1_ new percentage for recipient1 [PREC]
     */
    function setFeeTokenPctToRecipient1(uint256 feeTokenPctToRecipient1_) external onlyAuthorizedChanger {
        if (feeTokenPctToRecipient1_ > PRECISION) revert InvalidValue();
        feeTokenPctToRecipient1 = feeTokenPctToRecipient1_;
    }

    /**
     * @inheritdoc UUPSUpgradeable
     * @dev checks that the changer that will do the upgrade is currently authorized by governance to makes
     * changes within the system
     * @param newImplementation new implementation contract address(not used)
     */
    /* solhint-disable-next-line no-empty-blocks */
    function _authorizeUpgrade(address newImplementation) internal override onlyAuthorizedChanger {}

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */

    // Purposely left unused to save some state space to allow for future upgrades
    // slither-disable-next-line unused-state
    uint256[50] private __gap;
}

moc-main/contracts/collateral/rc20/MocCA.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.20;

import { MocCore } from "../../core/MocCore.sol";
import { MocOperations } from "../../core/MocOperations.sol";
import { MocBaseBucket } from "../../core/MocBaseBucket.sol";
import { MocQueueExecFees } from "../../queue/MocQueueExecFees.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title MocCARC20: Moc Collateral Asset RC20
 * @notice Moc protocol implementation using a RC20 as Collateral Asset.
 */
contract MocCARC20 is MocOperations {
    // ------- Structs -------
    struct InitializeParams {
        InitializeCoreParams initializeCoreParams;
        // Collateral Asset Token contract address
        address acTokenAddress;
    }

    // ------- Storage -------
    // Collateral Asset token
    IERC20 public acToken;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // ------- Initializer -------
    /**
     * @notice contract initializer
     * @param initializeParams_ contract initializer params
     * @dev governorAddress The address that will define when a change contract is authorized
     *      pauserAddress The address that is authorized to pause this contract
     *      acTokenAddress Collateral Asset Token contract address
     *      tcTokenAddress Collateral Token contract address
     *      mocFeeFlowAddress Moc Fee Flow contract address
     *      mocAppreciationBeneficiaryAddress Moc appreciation beneficiary address
     *      protThrld protected state threshold [PREC]
     *      liqThrld liquidation coverage threshold [PREC]
     *      feeRetainer pct retain on fees to be re-injected as Collateral, while paying fees with AC [PREC]
     *      tcMintFee additional fee pct applied on mint Collateral Tokens operations [PREC]
     *      tcRedeemFee additional fee pct applied on redeem Collateral Tokens operations [PREC]
     *      successFee pct of the gain because Pegged Tokens devaluation that is transferred
     *        in Collateral Asset to Moc Fee Flow during the settlement [PREC]
     *      appreciationFactor pct of the gain because Pegged Tokens devaluation that is returned
     *        in Pegged Tokens to appreciation beneficiary during the settlement [PREC]
     *      bes number of blocks between settlements
     *      tcInterestCollectorAddress TC interest collector address
     *      tcInterestRate pct interest charged to TC holders on the total collateral in the protocol [PREC]
     *      tcInterestPaymentBlockSpan amount of blocks to wait for next TC interest payment
     *      maxAbsoluteOpProviderAddress max absolute operation provider address
     *      maxOpDiffProviderAddress max operation difference provider address
     *      decayBlockSpan number of blocks that have to elapse for the linear decay factor to be 0
     *      emaCalculationBlockSpan amount of blocks to wait between Pegged ema calculation
     *      mocVendors address for MocVendors contract
     *      mocQueueAddress address for MocQueue contract
     */
    function initialize(InitializeParams calldata initializeParams_) external initializer {
        __MocCore_init(initializeParams_.initializeCoreParams);
        acToken = IERC20(initializeParams_.acTokenAddress);
    }

    // ------- Internal Functions -------

    /**
     * @inheritdoc MocCore
     * @dev this function could revert during safeTransfer call.
     *  safeTransfer will revert if token transfer reverts or returns 0
     */
    function acTransfer(address to_, uint256 amount_) internal override {
        if (amount_ > 0) {
            SafeERC20.safeTransfer(acToken, to_, amount_);
        }
    }

    /**
     * @inheritdoc MocOperations
     */
    function unlockACInPending(address owner_, uint256 qACToUnlock_) external override onlyMocQueue {
        unchecked {
            qACLockedInPending -= qACToUnlock_;
        }
        acTransfer(owner_, qACToUnlock_);
    }

    /**
     * @inheritdoc MocCore
     */
    function acBalanceOf(address account) internal view override returns (uint256 balance) {
        return acToken.balanceOf(account);
    }

    /**
     * @inheritdoc MocOperations
     */
    function _lockACInPending(uint256 qACToLock_) internal override {
        super._lockACInPending(qACToLock_);
        SafeERC20.safeTransferFrom(acToken, msg.sender, address(this), qACToLock_);
    }

    /**
     * @inheritdoc MocOperations
     */
    function _getExecFeeSent(
        uint256 qACmax_,
        MocQueueExecFees.OperType /*operType_*/
    ) internal override returns (uint256 qACmax, uint256 execFee) {
        // For RC20 solutions, msg.value should match exactly the execution Fee of the OperType
        return (qACmax_, msg.value);
    }

    // ------- External Functions -------

    /**
     * @notice caller sends Collateral Asset and recipient receives Collateral Token
     *  `vendor_` receives a markup in Fee Token if possible or in qAC if not
     *   Requires prior sender approval of Collateral Asset to this contract
     * @param qTC_ amount of Collateral Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param recipient_ address who receives the Collateral Token
     * @param vendor_ address who receives a markup, `0x0` if not using a vendor, `0x0` if not using a vendor
     * @return operId Identifier to track the Operation lifecycle
     */
    function mintTC(
        uint256 qTC_,
        uint256 qACmax_,
        address recipient_,
        address vendor_
    ) external payable returns (uint256 operId) {
        return _mintTCtoViaVendor(qTC_, qACmax_, recipient_, vendor_);
    }

    /**
     * @notice caller sends Collateral Asset and recipient receives Pegged Token
     *  `vendor_` receives a markup in Fee Token if possible or in qAC if not
     *   Requires prior sender approval of Collateral Asset to this contract
     * @param tp_ Pegged Token address to mint
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param recipient_ address who receives the Pegged Token
     * @param vendor_ address who receives a markup, `0x0` if not using a vendor
     * @return operId Identifier to track the Operation lifecycle
     */
    function mintTP(
        address tp_,
        uint256 qTP_,
        uint256 qACmax_,
        address recipient_,
        address vendor_
    ) external payable returns (uint256 operId) {
        return _mintTPtoViaVendor(tp_, qTP_, qACmax_, recipient_, vendor_);
    }

    /**
     * @notice caller sends Collateral Asset and recipient receives Collateral Token and Pegged Token
     *  `vendor_` receives a markup in Fee Token if possible or in qAC if not
     *  Requires prior sender approval of Collateral Asset to this contract
     *  This operation is done without checking coverage
     *  Collateral Token and Pegged Token are minted in equivalent proportions so that its price
     *  and global coverage are not modified.
     *  Reverts if qAC sent are insufficient.
     * @param tp_ Pegged Token address
     * @param qTP_ amount of Pegged Token to mint
     * @param qACmax_ maximum amount of Collateral Asset that can be spent
     * @param recipient_ address who receives the Collateral Token and Pegged Token
     * @param vendor_ address who receives a markup, `0x0` if not using a vendor
     * @return operId Identifier to track the Operation lifecycle
     */
    function mintTCandTP(
        address tp_,
        uint256 qTP_,
        uint256 qACmax_,
        address recipient_,
        address vendor_
    ) external payable returns (uint256 operId) {
        return _mintTCandTPtoViaVendor(tp_, qTP_, qACmax_, recipient_, vendor_);
    }

    /**
     * @notice caller sends a Pegged Token and recipient receives another one
     *  `vendor_` receives a markup in Fee Token if possible or in qAC if not
     * @param tpFrom_ owned Pegged Token address
     * @param tpTo_ target Pegged Token address
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTPmin_ minimum amount of target Pegged Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param recipient_ address who receives the target Pegged Token
     * @param vendor_ address who receives a markup, `0x0` if not using a vendor
     * @return operId Identifier to track the Operation lifecycle
     */
    function swapTPforTP(
        address tpFrom_,
        address tpTo_,
        uint256 qTP_,
        uint256 qTPmin_,
        uint256 qACmax_,
        address recipient_,
        address vendor_
    ) external payable returns (uint256 operId) {
        return _swapTPforTPtoViaVendor(tpFrom_, tpTo_, qTP_, qTPmin_, qACmax_, recipient_, vendor_);
    }

    /**
     * @notice caller sends a Pegged Token and recipient receives Collateral Token
     *  `vendor_` receives a markup in Fee Token if possible or in qAC if not
     * @param tp_ Pegged Token address
     * @param qTP_ amount of owned Pegged Token to swap
     * @param qTCmin_ minimum amount of Collateral Token that `recipient_` expects to receive
     * @param qACmax_ maximum amount of Collateral Asset that can be spent in fees
     * @param recipient_ address who receives the Collateral Token
     * @param vendor_ address who receives a markup, `0x0` if not using a vendor
     * @return operId Identifier to track the Operation lifecycle
     */
    function swapTPforTC(
        address tp_,
        uint256 qTP_,
        uint256 qTCmin_,
        uint256 qACmax_,
        address recipient_,
        address vendor_
    ) external payable returns (uint256 operId) {
        return _swapTPforTCtoViaVendor(tp_, qTP_, qTCmin_, qACmax_, recipient_, vendor_);
    }

    /**
     * @notice Caller sends a Collateral Token and recipient receives Pegged Token.
     *  `vendor_` receives a markup in Fee Token if possible or in qAC if not.
     * @param tp_ Pegged Token address
     * @param qTC_ Amount of owned Collateral Token to swap
     * @param qTPmin_ Minimum amount of Pegged Token that `recipient_` expects to receive
     * @param qACmax_ Maximum amount of Collateral Asset that can be spent in fees
     * @param recipient_ Address who receives the Pegged Token
     * @param vendor_ address who receives a markup, `0x0` if not using a vendor
     * @return operId Identifier to track the Operation lifecycle
     */
    function swapTCforTP(
        address tp_,
        uint256 qTC_,
        uint256 qTPmin_,
        uint256 qACmax_,
        address recipient_,
        address vendor_
    ) external payable returns (uint256 operId) {
        return _swapTCforTPtoViaVendor(tp_, qTC_, qTPmin_, qACmax_, recipient_, vendor_);
    }

    /**
     * @notice Refreshes the AC holdings for the Bucket
     * @dev Intended to be use as notification after an RC20 AC transfer to this contract
     */
    function refreshACBalance() public {
        uint256 unaccountedAcBalance = acBalanceOf(address(this)) - nACcb - qACLockedInPending;
        // On this implementation, AC token balance is nACcb plus AC locked on pending operations
        if (unaccountedAcBalance > 0) _depositAC(unaccountedAcBalance);
    }

    /**
     * @inheritdoc MocBaseBucket
     */
    function evalLiquidation() public virtual override {
        // Before evaluating Liquidation, we need to make sure injected collateral is accounted
        refreshACBalance();
        super.evalLiquidation();
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */

    // Purposely left unused to save some state space to allow for future upgrades
    // slither-disable-next-line unused-state
    uint256[50] private __gap;
}

